mapper.select:
SELECT COLUMN_NAME AS columnName, DATA_TYPE AS columnType, COLUMN_COMMENT AS columnComment, CHARACTER_MAXIMUM_LENGTH AS charLength, NUMERIC_PRECISION AS numberLength, IF(IS_NULLABLE = 'YES', true, false) AS isNullable FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_SCHEMA=#{schemaName} AND TABLE_NAME=#{tableName}
AND COLUMN_NAME NOT IN ('id','create_date','create_by','update_date','update_by','remarks','del_flag')
AND COLUMN_NAME NOT IN ('id','parent_id','parent_ids','level','sort','create_date','create_by','update_date','update_by','remarks','del_flag')

SELECT t.id, t.table_type, t.table_name, t.table_prefix, t.business_name, t.schema_name, t.module_name, t.package_name, t.author, t.generate_path, t.options, t.remarks, t.create_by, t.update_by, u1.nick_name AS create_user, u2.nick_name AS update_user, t.create_date, t.update_date, COUNT(tfc.id) AS field_count FROM sys_table_config t LEFT JOIN (select * from sys_table_field_config where del_flag = false order by sort DESC) tfc ON tfc.table_config_id = t.id LEFT JOIN sys_user u1 ON u1.id = t.create_by AND u1.del_flag = false LEFT JOIN sys_user u2 ON u2.id = t.update_by AND u2.del_flag = false WHERE TRUE
AND t.table_type = #{request.tableType}
AND t.table_name LIKE CONCAT('%',#{request.tableName},'%')
AND t.business_name LIKE CONCAT('%',#{request.businessName},'%')
AND t.schema_name = #{request.schemaName}
AND t.del_flag = #{request.delFlag}
GROUP BY t.id, t.table_type, t.table_name, t.table_prefix, t.business_name, t.schema_name, t.module_name, t.package_name, t.author, t.generate_path, t.options, t.remarks, t.create_by, t.update_by, u1.nick_name, u2.nick_name, t.create_date, t.update_date
ORDER BY t.update_date
ORDER BY t.update_date DESC

SELECT t.TABLE_NAME AS name, t.TABLE_SCHEMA AS schemaName, t.TABLE_COMMENT AS comment, t.TABLE_ROWS AS tableRows, t.CREATE_TIME AS createTime, t.UPDATE_TIME AS updateTime, CASE WHEN c1.COLUMN_NAME IS NOT NULL THEN 1 ELSE 0 END AS hasParentId, CASE WHEN c2.COLUMN_NAME IS NOT NULL THEN 1 ELSE 0 END AS hasDelFlag FROM INFORMATION_SCHEMA.TABLES t LEFT JOIN INFORMATION_SCHEMA.COLUMNS c1 ON c1.TABLE_SCHEMA = t.TABLE_SCHEMA AND c1.TABLE_NAME = t.TABLE_NAME AND c1.COLUMN_NAME = 'parent_id' LEFT JOIN INFORMATION_SCHEMA.COLUMNS c2 ON c2.TABLE_SCHEMA = t.TABLE_SCHEMA AND c2.TABLE_NAME = t.TABLE_NAME AND c2.COLUMN_NAME = 'del_flag' WHERE t.TABLE_SCHEMA = #{request.schemaName}
AND t.TABLE_NAME LIKE CONCAT('%',#{request.tableName},'%')
AND t.TABLE_NAME NOT LIKE 'qrtz_%' AND t.TABLE_NAME NOT LIKE 'quartz_%' ORDER BY t.TABLE_NAME DESC

SELECT
    COUNT(1)
FROM
    INFORMATION_SCHEMA.COLUMNS t
WHERE
    t.TABLE_SCHEMA = #{schemaName}
AND t.TABLE_NAME = #{tableName}
AND t.COLUMN_NAME = #{fieldName}
